<script>
	window.onload = function(){
	    top.hideNavHide();
	    $("body").width($(document).width()-240).fadeIn();
	}
	window.onresize = function(){
	    $("body").width($(document).width()-240);
	}
	window.onerror = function(){
	    setTimeout(function(){
	        $("body").fadeIn();
	        dr_tips("页面存在错误，请检查控制台报错信息！");
	    }, 2000);
	}
</script>